// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract Payment {
    address public admin;
    IERC20 public usdt;
    address public addresss;
    event PaymentDone(
        address payer,
        uint256 amount,
        uint256 paymentId,
        uint256 date
    );

    constructor(address adminAddress, address usdtAddress) {
        admin = adminAddress;
        usdt = IERC20(usdtAddress);
        addresss = usdtAddress;
    }

    function approveShow(uint256 amount) external {
        usdt.approve(msg.sender, amount);
    }

    function pay(uint256 amount) external {
        usdt.transfer(msg.sender, amount);
        // emit PaymentDone(msg.sender, amount, paymentId, block.timestamp);
    }

    function checkAddress() public view returns (address) {
        return msg.sender;
        // emit PaymentDone(msg.sender, amount, paymentId, block.timestamp);
    }

    function checkAddressThis() public view returns (address) {
        return address(this);
        // emit PaymentDone(msg.sender, amount, paymentId, block.timestamp);
    }

    function checkAddressCoin() public view returns (address) {
        return addresss;
        // emit PaymentDone(msg.sender, amount, paymentId, block.timestamp);
    }
}
